// PROCESS MEMORY PICKER OUTPUT AND PREPARE FOR KNOW IT ALL
const memoryResult = $json;
const originalData = $input.first().json;

console.log('ðŸ§  Processing Memory Picker Result...');

// Extract AI response and tool calls
let analysis = {};
let toolCalls = [];
let toolResults = [];

try {
  if (memoryResult.choices && memoryResult.choices[0]) {
    const choice = memoryResult.choices[0];

    // Get the message content (analysis)
    if (choice.message && choice.message.content) {
      try {
        // Try to parse as JSON first
        analysis = JSON.parse(choice.message.content);
      } catch (e) {
        // If not JSON, treat as text analysis
        analysis = {
          memory_action: 'store',
          entity_type: 'general',
          extracted_data: { content: choice.message.content },
          confidence: 0.7,
          next_step: 'generate_contextual_response'
        };
      }
    }

    // Get tool calls if any were made
    if (choice.message && choice.message.tool_calls) {
      toolCalls = choice.message.tool_calls;
      console.log('ðŸ”§ Tool calls made:', toolCalls.length);
    }
  }
} catch (error) {
  console.error('âŒ Error processing Memory Picker result:', error.message);
  analysis = { 
    error: error.message, 
    memory_action: 'none',
    entity_type: 'general',
    extracted_data: {},
    confidence: 0.1,
    next_step: 'generate_basic_response'
  };
}

// Enhance original data with memory analysis
const enhanced = {
  ...originalData,
  memory_analysis: analysis,
  tool_calls: toolCalls,
  tool_results: toolResults,
  memory_processed_at: new Date().toISOString(),
  ai_tokens_used: memoryResult.usage?.total_tokens || 0
};

console.log('âœ… Enhanced data for Know it all:', {
  memory_action: analysis.memory_action,
  entity_type: analysis.entity_type,
  tool_calls_count: toolCalls.length,
  confidence: analysis.confidence
});

return [{ json: enhanced }];

