// EXTRACT AND PREPARE FINAL ZARA RESPONSE
const knowItAllResult = $json;
const originalData = $input.first().json;

console.log('ðŸ¤– Processing Know it all response...');

let finalResponse = "Thanks for your message! I'm ZARA, your RPD assistant. I'm here to help you with your property development needs.";
let tokensUsed = 0;

try {
  if (knowItAllResult.choices && knowItAllResult.choices[0]) {
    const choice = knowItAllResult.choices[0];

    if (choice.message && choice.message.content) {
      finalResponse = choice.message.content.trim();
    }

    tokensUsed = knowItAllResult.usage?.total_tokens || 0;
  }
} catch (error) {
  console.error('âŒ Error extracting Know it all response:', error.message);
  finalResponse = "I encountered an issue processing your message. Could you please try again? I'm here to help with your RPD needs!";
}

// Ensure response is not too long for WhatsApp
if (finalResponse.length > 1500) {
  finalResponse = finalResponse.substring(0, 1450) + '...';
}

// Prepare final response data
const responseData = {
  ...originalData,
  ai_response: finalResponse,
  response_type: 'zara_ai_agent',
  generated_at: new Date().toISOString(),
  total_tokens_used: (originalData.ai_tokens_used || 0) + tokensUsed,
  memory_confidence: originalData.memory_analysis?.confidence || 0.5
};

console.log('âœ… Final ZARA AI Response prepared:', {
  response_length: finalResponse.length,
  session_id: responseData.session_id,
  total_tokens: responseData.total_tokens_used,
  confidence: responseData.memory_confidence
});

return [{ json: responseData }];
