// PARSE INCOMING WHATSAPP MESSAGE FOR MEMORY SYSTEM
const data = $json || {};

console.log('ðŸ“¨ Processing WhatsApp message for ZARA:', JSON.stringify(data, null, 2));

// Extract WhatsApp data and format for Memory Picker
const parsed = {
  // Core message data
  from: data.From || '',
  to: data.To || '',
  body: data.Body || '',

  // WhatsApp specific
  wa_id: data.WaId || '',
  message_sid: data.MessageSid || '',

  // Media handling
  num_media: parseInt(data.NumMedia || '0'),
  media_urls: [],

  // Format for AI agents
  chat_input: data.Body || '',
  user_id: data.From || data.WaId || '',
  session_id: `wa_${data.WaId || data.From}`,

  // Metadata
  timestamp: new Date().toISOString(),
  direction: 'inbound',
  status: 'received',

  // Raw data for debugging
  raw_webhook: data
};

// Extract media URLs if present
if (parsed.num_media > 0) {
  for (let i = 0; i < parsed.num_media; i++) {
    const mediaUrl = data[`MediaUrl${i}`];
    if (mediaUrl) parsed.media_urls.push(mediaUrl);
  }
}

console.log('âœ… Parsed for ZARA Memory System:', {
  from: parsed.from,
  body: parsed.body,
  chat_input: parsed.chat_input,
  session_id: parsed.session_id
});

return [{ json: parsed }];

